mapper.update:
update event
set e_read_cnt=e_read_cnt+1
where e_num=#{e_num}

update event
set e_result=#{e_result}
where e_num=#{e_num}

mapper.select:
select e_num, e_name, e_agency, e_result from event
where e_email=#{e_email}
order by e_num

